var url="",inputSearch=document.querySelector(".input-search"),btSearch=document.querySelector(".bt-search");btSearch.addEventListener("click",function(){var e=inputSearch.value;url="https://en.wikipedia.org/w/api.php?action=opensearch&search="+e+"&format=json&callback=?",$.ajax({url:url,type:"GET",async:!1,dataType:"json",success:function(e,a,t){console.log(e);for(var r=0;r<e[1].length;r++)$("#main-content").prepend('<article class="article"><h2><a href="'+e[3][r]+' "class="article-title" target="_blank">'+e[1][r]+'</a></h2><p><a href="'+e[3][r]+'"class="article-text" target="_blank">'+e[2][r]+"</a></p></article>")}})});